<?php// on définit notre balise title$titleAdminCrud = "Administration";$title = "Administration";// on inclut notre package (librairie) qui s'occupe de charger toutes les pages dont on a besoinrequire_once("../include/require.php");// on apelle à chaque fois une fonction qui nous retourne la liste de toutes les// resultats qui appartient à leur classes respective// et on les stocke à chaque fois dans une variable qui leur est approprié$elementList = Bdd::getInstance()->conn->query("SELECT *, element.id as theId, categorie.nom as theCat, 	sous_categorie.nom as theSubCat, sous_sous_categorie.nom as theSubSubCat, type.nom as theType FROM `element` 		INNER JOIN categorie ON element.id_categorie = categorie.id         INNER JOIN sous_categorie ON element.id_sous_categorie = sous_categorie.id         INNER JOIN sous_sous_categorie ON element.id_sous_sous_categorie = sous_sous_categorie.id        INNER JOIN type ON element.id_type = type.id");$quizList = Bdd::getInstance()->conn->query("SELECT *, quiz.id as theId, categorie_quiz.nom as theCat FROM `quiz` 		INNER JOIN categorie_quiz ON quiz.id_categorie = categorie_quiz.id");// on inclut la vue (partie visible => front) de la pagerequire_once("views/index.view.php");?>